import React from 'react';

function TodoItem({ todo, toggleTodo }) {
  return (
    <div>
      <input
        type="checkbox"
        checked={todo.completed}
        onChange={() => toggleTodo(todo.id)} // Вызываем функцию при изменении
      />
      <span>{todo.text}</span> {/* Изменили на todo.text для правильного отображения */}
    </div>
  );
}

TodoItem.defaultProps

export default TodoItem;
