import React, { useState } from 'react';
import TodoItem from '../components/TodoItem';
import SwitchButton from '../components/SwitchButton';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function DndPage() {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');
  const [filter, setFilter] = useState('all'); // all, completed, incomplete

  const handleAddTask = () => {
    if (task.trim()) {
      setTasks([...tasks, { id: tasks.length+1, text: task, completed: false }]);
      setTask('');
    }
  };

  const handleFilterChange = (filterType) => {
    setFilter(filterType);
  };

  const filteredTasks = tasks.filter((task) => {
    if (filter === 'completed') {
      return task.completed;
    } else if (filter === 'incomplete') {
      return !task.completed;
    }
    return true;
  });

  const handleOnDragEnd = (result) => {
    const { source, destination } = result;

    // If there's no destination (e.g., dropped outside the list), do nothing
    if (!destination) return;

    // Reorder the tasks based on drag result
    const reorderedTasks = Array.from(tasks);
    const [removed] = reorderedTasks.splice(source.index, 1);
    reorderedTasks.splice(destination.index, 0, removed);

    setTasks(reorderedTasks);
  };

  return (
      <div className="App">
      <h1>To-Do List</h1>
      <SwitchButton path="/"/>
      <div className="task-input">
        <form onSubmit={(e) => {e.preventDefault(); handleAddTask();}}>
          <input
            type="text"
            value={task}
            onChange={(e) => setTask(e.target.value)}
            placeholder="Add a new task"
          />
          <button onClick={handleAddTask}>Add</button>
        </form>
      </div>

      <div className="filters">
        <button onClick={() => handleFilterChange('all')}>All</button>
        <button onClick={() => handleFilterChange('completed')}>Completed</button>
        <button onClick={() => handleFilterChange('incomplete')}>Incomplete</button>
      </div>
    <div className='noselect'>
    <DragDropContext onDragEnd={handleOnDragEnd}>
      <Droppable droppableId='tasks'>
      {(provided) => (
      <ul {...provided.droppableProps} ref={provided.innerRef}>
        {filteredTasks.map((task, index) => (
           <Draggable key={task.text} draggableId={String(index)} index={index}>
             {(provided) => (
            <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
            <TodoItem
              task={task}
              index={index}
              tasks={tasks}
              setTasks={setTasks}
            />
            </div>
           )}
           </Draggable>
        ))}
        {provided.placeholder}
      </ul>
      )}
      </Droppable>
    </DragDropContext>
    </div>
    </div>
  );
}

export default DndPage;
