import React, { useState } from 'react';
import TodoItem from '../components/TodoItem';
import SwitchButton from '../components/SwitchButton';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function DndPage() {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');

  const handleAddTask = () => {
    if (task.trim()) {
      setTasks([...tasks, { text: task, status: 'todo' }]);
      setTask('');
    }
  };

  const handleOnDragEnd = (result) => {
    console.log(tasks);
    console.log(result);
    const { source, destination } = result;

    // If there's no destination (e.g., dropped outside the list), do nothing
    if (!destination) return;

    // If the source and destination are the same, do nothing
    if (source.droppableId === destination.droppableId && source.index === destination.index) return;

    const updatedTasks = Array.from(tasks);
    const [movedTask] = updatedTasks.splice(source.index, 1);

    // Update the task's status based on the column it's dropped into
    movedTask.status = destination.droppableId;
    updatedTasks.splice(destination.index, 0, movedTask);
    console.log(tasks);
    setTasks(updatedTasks);
  };


  // Filter tasks based on their status for each column
  const todoTasks = tasks.filter(task => task.status === 'todo');
  const doneTasks = tasks.filter(task => task.status === 'done');
  const blockedTasks = tasks.filter(task => task.status === 'blocked');

  return (
    <div className="App">
      <h1>To-Do List</h1>
      <SwitchButton path="/" />
      <div className="task-input">
        <form onSubmit={(e) => { e.preventDefault(); handleAddTask(); }}>
          <input
            type="text"
            value={task}
            onChange={(e) => setTask(e.target.value)}
            placeholder="Add a new task"
          />
          <button onClick={handleAddTask}>Add</button>
        </form>
      </div>

      <div className="columns">
        <DragDropContext onDragEnd={handleOnDragEnd}>
          {/* To Do Column */}
          <div className="column">
            <h2>To Do</h2>
            <Droppable droppableId="todo">
              {(provided) => (
                <ul {...provided.droppableProps} ref={provided.innerRef}>
                  {todoTasks.map((task, index) => (
                    <Draggable key={task.text} draggableId={task.text} index={index}>
                      {(provided) => (
                        <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                          <TodoItem
                            task={task}
                            index={index}
                            tasks={tasks}
                            setTasks={setTasks}
                          />
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </ul>
              )}
            </Droppable>
          </div>

          {/* Done Column */}
          <div className="column">
            <h2>Done</h2>
            <Droppable droppableId="done">
              {(provided) => (
                <ul {...provided.droppableProps} ref={provided.innerRef}>
                  {doneTasks.map((task, index) => (
                    <Draggable key={task.text} draggableId={task.text} index={index}>
                      {(provided) => (
                        <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                          <TodoItem
                            task={task}
                            index={index}
                            tasks={tasks}
                            setTasks={setTasks}
                          />
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </ul>
              )}
            </Droppable>
          </div>

          {/* Blocked Column */}
          <div className="column">
            <h2>Blocked</h2>
            <Droppable droppableId="blocked">
              {(provided) => (
                <ul {...provided.droppableProps} ref={provided.innerRef}>
                  {blockedTasks.map((task, index) => (
                    <Draggable key={task.text} draggableId={task.text} index={index}>
                      {(provided) => (
                        <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                          <TodoItem
                            task={task}
                            index={index}
                            tasks={tasks}
                            setTasks={setTasks}
                          />
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </ul>
              )}
            </Droppable>
          </div>
        </DragDropContext>
      </div>
    </div>
  );
}

export default DndPage;
