import React, { useState } from 'react';
import TodoItem from '../components/TodoItem';
import SwitchButton from '../components/SwitchButton';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function DndPage() {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');

  const handleAddTask = () => {
    if (task.trim()) {
      setTasks([...tasks, { text: task, status: 'incomplete' }]);
      setTask('');
    }
  };

  const filteredTasks = tasks.filter((task) => {
    if (filter === 'completed') {
      return task.status === 'done';
    } else if (filter === 'incomplete') {
      return task.status === 'incomplete';
    }
    return true;
  });

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const updatedTasks = [...tasks];
    const [movedTask] = updatedTasks.splice(result.source.index, 1);
    movedTask.status = result.destination.droppableId; // Update the task status based on the column
    updatedTasks.splice(result.destination.index, 0, movedTask);
    setTasks(updatedTasks);
  };

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <div className="App">
        <h1>To-Do List</h1>
        <SwitchButton path="/" />
        <div className="task-input">
          <form onSubmit={(e) => { e.preventDefault(); handleAddTask(); }}>
            <input
              type="text"
              value={task}
              onChange={(e) => setTask(e.target.value)}
              placeholder="Add a new task"
            />
            <button onClick={handleAddTask}>Add</button>
          </form>
        </div>

        <div className="columns">
            

          <Droppable droppableId="done">
            {(provided) => (
              <div {...provided.droppableProps} ref={provided.innerRef}>
                <h2>Done</h2>
                {filteredTasks.filter(task => task.status === 'done').map((task, index) => (
                  <Draggable key={index} draggableId={task.text} index={index}>
                    {(provided) => (
                      <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                        <TodoItem task={task} index={index} tasks={tasks} setTasks={setTasks} />
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>

          <Droppable droppableId="incomplete">
            {(provided) => (
              <div {...provided.droppableProps} ref={provided.innerRef}>
                <h2>Incomplete</h2>
                {filteredTasks.filter(task => task.status === 'incomplete').map((task, index) => (
                  <Draggable key={index} draggableId={task.text} index={index}>
                    {(provided) => (
                      <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                        <TodoItem task={task} index={index} tasks={tasks} setTasks={setTasks} />
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>

          <Droppable droppableId="blocked">
            {(provided) => (
              <div {...provided.droppableProps} ref={provided.innerRef}>
                <h2>Blocked</h2>
                {filteredTasks.filter(task => task.status === 'blocked').map((task, index) => (
                  <Draggable key={index} draggableId={task.text} index={index}>
                    {(provided) => (
                      <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                        <TodoItem task={task} index={index} tasks={tasks} setTasks={setTasks} />
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
      </div>
    </DragDropContext>
  );
}

export default DndPage;
